      bottom: 10px;
      margin-left: -10%;
      transform: scaleX(0);
      transition-delay: .3s;
      background: #1e1109;
      transition: all .3s ease;
    }

    a {
      display: inline-block;
      position: relative;
      transition: all .3s ease;

      &:before {
        content: "";
        position: absolute;
        left: 0;
        top: 0;
        background: #000;
        width: 2px;
        height: 100%;
        transition: all .3s ease;
      }

      &:after {
        content: "";
        position: absolute;
        left: 0;
        top: 0;
        background: #000;
        width: 100%;
        height: 2px;
        transition: all .3s ease;
      }

      span {
        padding: 5px 15px;
        z-index: 1;
        position: relative;
        display: inline-block;

        &:before {
          content: "";
          position: absolute;
          right: 0;
          bottom: 0;
          background: #000;
          width: 2px;
          height: 100%;
          transition: all .3s ease;
        }

        &:after {
          content: "";
          position: absolute;
          right: 0;
          bottom: 0;
          background: #000;
          width: 100%;
          height: 2px;
          transition: all .3s ease;
        }
      }
    }

    &:hover {
      &:after {
        transform: scaleX(1);
        transition-delay: .3s;
      }

      a:before, span:before {
        animation: testHeightBorder 0.5s ease;
        height: 0%;
      }

      a:after, span:after {
        animation: testWidthBorder 0.5s ease;
        width: 0%;
      }
    }
  }

  .row {
    padding: 0 85px;
    margin-bottom: 30px;
    @include flex-jc-between;

    .event-box {
      height: 450px;
      width: 300px;
      position: relative;
      margin: 20px;
      margin-bottom: 20px;
      box-sizing: border-box;
      flex: 0 0 auto;

      &:before {
        content: "";
        position: absolute;
        bottom: 0;
        width: 50%;
        height: 1px;
        left: 50%;
        margin-left: -50%;
        background: #0089A7;
      }

      &:after {
        content: "";
        position: absolute;
        bottom: 0;
        width: 50%;
        height: 1px;
        right: 50%;
        margin-right: -50%;
        background: #cd4a38;
      }

      a {
        color: #000;
        text-decoration: none;

        h3 {
          padding: 20px 0px;
          font-size: 15px;
          font-weight: 500;
        }

        .event-box-txt {
          font-size: 15px;
          color: #636363;
        }

        .thumb {
          position: relative;
          margin-bottom: 20px;
          text-align: center;

          .new {
            position: absolute;
            left: 0;
            top: 0;
            display: block;
            background: #0089A7;
            color: #fff;
            width: 60px;
            height: 60px;
            line-height: 60px;
            border-radius: 50%;
            text-align: center;
            z-index: 10;
            font-size: 14px;

            &:after {
              content: "";
              position: absolute;
              left: -4px;
              top: -4px;
              width: 64px;
              height: 64px;
              border-radius: 50%;
              border: 2px solid #0089A7;
            }
          }

          .mask {
            width: 300px;
            height: 256px;
            margin: 0 auto;

            mask: url(../images/icons/hexagon.png) no-repeat top center;
            background-size: contain;

            img {
              width: 400px;
              line-height: 1;
              font-size: 0;
              height: 100%;
              max-width: 82%;

              &:hover {
                opacity: .6;
              }
            }
          }

          .cat {
            position: absolute;
            left: 0;
            bottom: 0;
            display: inline-block;
            background: #1e1109;
            color: #fff;
            font-size: 13px;
            padding: 3px 10px;
            text-align: center;
            z-index: 1000;
          }
        }

        .date {
          text-align: center;
          font-size: 15px;
          font-family: "Crimson Text",serif;
          margin-bottom: 10px;
          @include flex-al-c;

          span {
            font-size: 20px;
            color: #0089A7;
            padding: 0 3px;
          }
        }
      }
    }
  }
}

@keyframes testHeightBorder {
  from{
      height: 100%;
  }

  to{
      height: 0;
  }
}

@keyframes testWidthBorder {
  from{
      width: 100%;
  }

  to{
      width: 0;
  }
}

