    z-index: 999;
    transition: .5s;

    &_bgcolor {
      background-color: #0089A7;
    }

    &-logo {
      width: 100px;
      padding: 13px 0;
    }

    &-name {
      font-size: 24px;
      color: #fff;
      font-weight: bold;
      text-shadow: black 0.1em 0.1em 0.2em;
    }

    &-list {
      @include flex-jc-between;
      height: 100%;
      width: 320px;
    }

    &-option {
      margin: 0 20px;
      position: relative;
      cursor: pointer;
      color: #fff;
      text-shadow: black 0.1em 0.1em 0.2em;

      &::before {
        content: "";
        position: absolute;
        left: 0;
        bottom: -5px;
        width: 100%;
        height: 1px;
        background-color: #000;
        transform: scalex(0);
        transition: all .3s ease;
      }

      &:hover {
        &::before {
          transform: scalex(1);
        }
      }
    }


  }

  .fullscreen {
    height: 100%;
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
  }

  .down-box {
    position: absolute;
    bottom: 3%;
    left: 50%;
    z-index: 3;
    width: 40px;
    height: 40px;
    background-image: url(../images/icons/download-white.svg);
    background-position: center;
    background-size: cover;
    transform: translateX(-50%);
    animation-duration: 2s;
    animation-iteration-count: infinite;
    animation-fill-mode: both;
    animation-name: bounce;
    transform-origin: center bottom;

    .icon {
      width: 30px;
      height: 30px;
      border-radius: 50%;
      padding: 10px;
      border: 3px solid #000;
    }
  }

  .sliders {
    height: 100%;
    background-color: #9e9e9e;
    margin: 0;
    position: absolute;
    top: 0;
    bottom: 0;
    width: 100%;

    &-option {
      opacity: 0;
      position: absolute;
      top: 0;
      left: 0;
      z-index: 1;
      width: 100%;
      height: inherit;
      overflow: hidden;
      transition: .6s;

      &_show {
        opacity: 2;

        .sliders-textbox {
          left: 200px;
          opacity: 2;
        }
      }

      img {
        height: 100%;
        width: 100%;
        position: absolute;
        top: 0;
        bottom: 0;
        background-attachment: fixed;
        background-position: center;
        background-repeat: no-repeat;
        background-size: cover;
      }
    }

    .img-1 {
      background-image: url("../images/life/IMG_6218.JPG");
    }

    .img-2 {
      background-image: url("../images/life/IMG_9527.JPG");
    }

    &-textbox {
      position: absolute;
      top: 150px;
      left: 100px;
      opacity: 0;
      transition: .6s;
    }

    &-txt {
      color: #fff;
      font-size: 30px;
      padding-bottom: 10px;
      text-shadow: black 0.1em 0.1em 0.2em;

      &_bold {
        font-size: 40px;
        font-weight: bold;
      }
    }
  }
}

