.content {
  width: 100%;
  position: relative;
  background-color: #f5f5f5;
  padding-top: 100px;

  .event {
    position: relative;

    &-list {
      width: calc(100% - 200px);
      padding: 10px 0;
      border-top: 3px solid #fff;
      border-bottom: 3px solid #fff;
      margin: 20px 100px;
      @include flex-jc-between;


    }

    &-option {
      margin: 10px;
      cursor: pointer;

      &-txt {
        font-size: 15px;
        color: #000;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;

        &:before {
          content: "#";
          color: #0089A7;
        }
      }

      &:hover {
        p {
          color: #0089A7;
        }
      }
    }
  }

  .title {
    display: block;
    padding: 0 20px 20px 20px;
    position: relative;
    height: 70px;

    &_top {
      margin-top: 60px;
    }

    &-txt {
      font-size: 15px;
      text-align: center;
      padding-bottom: 10px;

      &_big {
        font-size: 35px;
        letter-spacing: 3px;

        &:before {
          content: "";
          position: absolute;
          bottom: 0;
          width: 40px;
          height: 3px;
          left: 50%;
          margin-left: -30px;
          background: #0089A7;
        }

        &:after {
          content: "";
          position: absolute;
          bottom: 0;
          width: 40px;
          height: 3px;
          right: 50%;
          margin-right: -30px;
          background: #00AA90;
        }
      }

      &_border {
        &:before {
          left: auto;
          top: -25px;
        }

        &:after {
          right: auto;
        }
      }
    }
  }

  .all-btn {
    display: inline-block;
    text-align: center;
    padding: 10px;
    position: absolute;
    top: -10px;
    right: 100px;

    &:after {
      content: "";
      position: absolute;
      width: 20%;
      height: 2px;
      left: 50%;
      bottom: 10px;
      margin-left: -10%;
      transform: scaleX(0);
      transition-delay: .3s;
      background: #1e1109;
      transition: all .3s ease;
    }

    a {
      display: inline-block;
      position: relative;
      transition: all .3s ease;

      &:before {
        content: "";
        position: absolute;
        left: 0;
        top: 0;
        background: #000;
        width: 2px;
        height: 100%;
        transition: all .3s ease;
      }

      &:after {
        content: "";
        position: absolute;
        left: 0;
        top: 0;
        background: #000;
        width: 100%;
        height: 2px;
        transition: all .3s ease;
      }

      span {
        padding: 5px 15px;
        z-index: 1;
        position: relative;
        display: inline-block;

        &:before {
          content: "";
          position: absolute;
          right: 0;
          bottom: 0;
          background: #000;
          width: 2px;
          height: 100%;
          transition: all .3s ease;
        }

        &:after {
          content: "";
          position: absolute;
          right: 0;
          bottom: 0;
          background: #000;
          width: 100%;
          height: 2px;
          transition: all .3s ease;
        }
      }
    }

    &:hover {
      &:after {
        transform: scaleX(1);
        transition-delay: .3s;
      }

      a:before, span:before {
        animation: testHeightBorder 0.5s ease;
        height: 0%;
      }

      a:after, span:after {
        animation: testWidthBorder 0.5s ease;
        width: 0%;
      }
    }
  }

  .row {
    padding: 0 85px;
    margin-bottom: 30px;
    @include flex-jc-between;

    .event-box {
      height: 450px;
      width: 300px;
      position: relative;
      margin: 20px;
      margin-bottom: 20px;
      box-sizing: border-box;
      flex: 0 0 auto;

      &:before {
        content: "";
        position: absolute;
        bottom: 0;
        width: 50%;
        height: 1px;
        left: 50%;
        margin-left: -50%;
        background: #0089A7;
      }

      &:after {
        content: "";
        position: absolute;
        bottom: 0;
        width: 50%;
        height: 1px;
        right: 50%;
        margin-right: -50%;
        background: #cd4a38;
      }

      a {
        color: #000;
        text-decoration: none;

        h3 {
          padding: 20px 0px;
          font-size: 15px;
          font-weight: 500;
        }

        .event-box-txt {
          font-size: 15px;
          color: #636363;
        }

        .thumb {
          position: relative;
          margin-bottom: 20px;
          text-align: center;

          .new {
            position: absolute;
            left: 0;
            top: 0;
            display: block;
            background: #0089A7;
            color: #fff;
            width: 60px;
            height: 60px;
            line-height: 60px;
            border-radius: 50%;
            text-align: center;
            z-index: 10;
            font-size: 14px;

            &:after {
              content: "";
              position: absolute;
              left: -4px;
              top: -4px;
              width: 64px;
              height: 64px;
              border-radius: 50%;
              border: 2px solid #0089A7;
            }
          }

          .mask {
            width: 300px;
            height: 256px;
            margin: 0 auto;

            mask: url(../images/icons/hexagon.png) no-repeat top center;
            background-size: contain;

            img {
              width: 400px;
              line-height: 1;
              font-size: 0;
              height: 100%;
              max-width: 82%;

              &:hover {
                opacity: .6;
              }
            }
          }

          .cat {
            position: absolute;
            left: 0;
            bottom: 0;
            display: inline-block;
            background: #1e1109;
            color: #fff;
            font-size: 13px;
            padding: 3px 10px;
            text-align: center;
            z-index: 1000;
          }
        }

        .date {
          text-align: center;
          font-size: 15px;
          font-family: "Crimson Text",serif;
          margin-bottom: 10px;
          @include flex-al-c;

          span {
            font-size: 20px;
            color: #0089A7;
            padding: 0 3px;
          }
        }
      }
    }
  }

  .works {
    position: relative;

    &-list {
      @include flex-c;
      flex-wrap: wrap;
      margin-top: 20px;

      &::before {
        display: table;
      }

      &-box {
        background: #fff;
        border-bottom: 2px solid #0089A7;
        position: relative;
        width: 500px;
        margin: 0 20px;
        margin-bottom: 40px;
        cursor: pointer;

        a {
          color: #000;

          .default {
            position: relative;
            z-index: 10;
            transition: all .4s cubic-bezier(.42,0,.58,1);

            img {
              line-height: 1;
              font-size: 0;
              height: 300px;
              width: 500px;
            }
          }

          .hover {
            position: absolute;
            left: 0;
            top: 0;
            z-index: 1;

            img {
              line-height: 1;
              font-size: 0;
              height: 300px;
              width: 500px;
            }
          }

          .text-box {
            width: calc(100% - 20px);
            padding: 10px;

            &-content {
              font-size: 20px;
              padding: 20px 0;
            }

            &-txt {
              font-size: 15px;
              color: #636363;
            }
          }
        }

        &:hover {
          a {
            .default {
              opacity: 0;
            }

            .hover {
              opacity: 1;
            }
          }
        }
      }
    }
  }
}

@keyframes testHeightBorder {
  from{
      height: 100%;
  }

  to{
      height: 0;
  }
}

@keyframes testWidthBorder {
  from{
      width: 100%;
  }

  to{
      width: 0;
  }
}

@keyframes bounce {
  0%, 20%, 53%, 80%, 100% {
    animation-timing-function: cubic-bezier(.215,.61,.355,1);
    transform: translateZ(0);
  }

  40%, 43% {
    animation-timing-function: cubic-bezier(.755,.05,.855,.06);
    transform: translate3d(0,-30px,0);
  }

  70% {
    animation-timing-function: cubic-bezier(.755,.05,.855,.06);
    transform: translate3d(0,-15px,0);
  }
  90% {
    transform: translate3d(0,-4px,0);
  }
}

sources.style/public-flex.scss:
@mixin flex {
  display: flex;
}

@mixin flex-c {
  display: flex;
  justify-content: center;
  align-items: center;
}

@mixin flex-al-c {
  display: flex;
  align-items: center;
}

@mixin flex-jc-c {
  display: flex;
  justify-content: center;
}

@mixin flex-jc-left {
  display: flex;
  align-items: center;
  justify-content: flex-start;
}

@mixin flex-jc-right {
  display: flex;
  align-items: center;
  justify-content: flex-end;
}

@mixin flex-jc-between {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

@mixin flex-jc-around {
  display: flex;
  align-items: center;
  justify-content: space-around;
}
